import { useEffect } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import "./grade-personagem.css";
import CardPersonagem from "./card-personagem.componente";
import { characterData } from '../../redux/characterReducer'
import { chracterList } from '../../redux/actionCharacter';


import star from '../../images/star.png'
import starFilled from '../../images/star-filled.png'
/**
 * Grade de personagens para a página inicial
 *
 * Você precisará adicionar as funções necessárias para exibir e paginar os personagens
 *
 *
 * @returns Elemento JSX
 */
const GradePersonagem = () => {
  const dispatch = useDispatch()

  let data = useSelector((state) => state.characterData)
  console.log('character data in gradePersonagem', data)

  useEffect(() => {
    dispatch(chracterList())
  }, [])

  const src = isFavorito ? starFilled : star
  return (
    <div className="grade-personagens">
      {

        data.map((data) =>
          <div>
            <CardPersonagem key={data.id} data={data} />

            <div className="botao-favorito">
              <button className="botao-favorito"
                onClick={() => dispatch(addCharacter(data))}>
                <img src={src} alt={"favorito"} />
              </button>
            </div>
          </div>
        )
      }
    </div>
  );
};

export default GradePersonagem;
