import { useSelector } from 'react-redux'
import CardPersonagem from '../componentes/personagens/card-personagem.componente';
/**
 * Esta é a página de favoritos. Aqui você deve ver todos os personagens marcados como favoritos
 *
 * Uso:
 * ``` <PaginaFavoritos /> ```
 *
 * @returns Página de favoritos
 */
const PaginaFavoritos = () => {

  const favoriteData = useSelector((state) => state.favoriteData)


  return (
    <div className="container">
      <div className="actions">
        <h3>Personagens Favoritos</h3>
        <button className="danger">Limpar favoritos</button>
      </div>
      <div>
        <table>
          <thead>
            <tr>
              <td>Personagem</td>
              <td>Imagem</td>
            </tr>
          </thead>
          <tbody>
            {
              favoriteData.map((favorite) =>
                <tr key={favorite.id}>
                  <td>{favorite.name}</td>
                  <td>{favorite.image}</td>
                </tr>
              )
            }
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default PaginaFavoritos;
